import React, { useEffect, useContext, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import Noteitem from "./Noteitem";
import Notecontext from "../context/notes/NotesContext";

export default function Content(props) {
  const a = useContext(Notecontext);
  const navigate = useNavigate();

  useEffect(() => {
    if (localStorage.getItem("token")) {
      a.getNotes();
    } else {
      navigate("/");
    }
  }, [a, navigate]);

  const [disable, setDisable] = useState(false);
  const [newvalue, setNewvalue] = useState();
  const [isSubmitting, setIsSubmitting] = useState(false); // Dynamic feedback for button
  const [isCameraActive, setIsCameraActive] = useState(false); // Toggle camera visibility

  const handleonclick = async (note) => {
    setNewvalue(note);
    await a.getuser();
    setDisable(true);
  };

  const handleonclick2 = async (e) => {
    e.preventDefault();
    setIsSubmitting(true); // Show loading state
    if (handleonclick) {
      const c = a.user._id;
      await newvalue.voteCount++;
      await a.updatecandidate(newvalue._id, newvalue.name, newvalue.party, newvalue.voteCount);
      await a.updateuser(c, a.user.name, a.user.email, a.user.aadharNumber, a.user.isvoted);
      setDisable(false);
      localStorage.removeItem("token");
      stopCapture();
      navigate("/end");
      props.handleonClick2();
    }
    setIsSubmitting(false); // Hide loading state
  };

  // Camera live
  const videoRef = useRef(null);
  const streamRef = useRef(null);

  useEffect(() => {
    navigator.mediaDevices.getUserMedia({ video: true })
      .then((stream) => {
        videoRef.current.srcObject = stream;
        streamRef.current = stream;
        setIsCameraActive(true); // Activate camera overlay
      })
      .catch((err) => {
        console.error("Error accessing the camera: ", err);
        alert("Could not access the camera. Please allow camera permissions.");
      });
  }, []);

  const stopCapture = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => track.stop());
      videoRef.current.srcObject = null;
      streamRef.current = null;
      setIsCameraActive(false); // Deactivate camera overlay
    }
  };

  return (
    <>
      <div className="container mx-auto py-4 flex flex-col items-center">
        <h1 className="text-3xl font-bold text-center mb-6 text-blue-700">
          Select Your Candidate
        </h1>

        {/* Candidate Cards */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 w-full justify-items-center">
          {a.notes.map((note) => {
            return (
              <div key={note._id} className="w-full max-w-sm">
                <Noteitem
                  note={note}
                  name={note.name}
                  party={note.party}
                  handleonClick2={props.handleonClick2}
                  handleonclick={handleonclick}
                  disable={disable}
                />
              </div>
            );
          })}
        </div>

        {/* Submit Button with Dynamic Effects */}
        <div className="mt-6">
          <button
            className={`btn btn-primary px-5 py-2 rounded-lg transition-transform transform-gpu ${
              disable ? "opacity-50 cursor-not-allowed" : "hover:scale-105"
            } ${isSubmitting ? "bg-green-500" : "bg-blue-500"}`}
            onClick={handleonclick2}
            disabled={disable || isSubmitting}
          >
            {isSubmitting ? "Submitting..." : "Submit"}
          </button>
        </div>
      </div>

      {/* Camera Overlay with Dynamic Effects */}
      {isCameraActive && (
        <div className="fixed inset-0 flex justify-center items-center bg-black bg-opacity-50 z-50">
          <video
            ref={videoRef}
            width="320"
            height="240"
            autoPlay
            className="border-4 border-blue-500 rounded-lg shadow-lg transform transition-opacity duration-500 ease-in-out"
          />
          <button
            onClick={stopCapture}
            className="absolute top-4 right-4 bg-red-600 text-white py-2 px-4 rounded-lg hover:bg-red-700"
          >
            Stop Camera
          </button>
        </div>
      )}
    </>
  );
}
