import React, { useEffect, useContext, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import Noteitem from "./Noteitem";
import Notecontext from "../context/notes/NotesContext";

export default function Content(props) {
  const a = useContext(Notecontext);
  const navigate = useNavigate();

  const [disable, setDisable] = useState(false);
  const [newvalue, setNewvalue] = useState();
  const [isSubmitting, setIsSubmitting] = useState(false); // Dynamic button feedback
  const [selectedNote, setSelectedNote] = useState(null); // Highlight selected candidate

  useEffect(() => {
    if (localStorage.getItem("token")) {
      a.getNotes();
    } else {
      navigate("/");
    }
  }, [a, navigate]);

  const handleonclick = async (note) => {
    setNewvalue(note);
    setSelectedNote(note._id); // Mark the note as selected
    await a.getuser();
    setDisable(true);
  };

  const handleonclick2 = async (e) => {
    e.preventDefault();
    setIsSubmitting(true); // Start the loading state
    if (handleonclick) {
      const c = a.user._id;
      await newvalue.voteCount++;
      await a.updatecandidate(newvalue._id, newvalue.name, newvalue.party, newvalue.voteCount);
      await a.updateuser(c, a.user.name, a.user.email, a.user.aadharNumber, a.user.isvoted);
      setDisable(false);
      localStorage.removeItem("token");
      stopCapture();
      navigate("/end");
      props.handleonClick2();
    }
    setIsSubmitting(false); // Stop loading state
  };

  // Camera live
  const videoRef = useRef(null);
  const streamRef = useRef(null);

  useEffect(() => {
    navigator.mediaDevices.getUserMedia({ video: true })
      .then((stream) => {
        videoRef.current.srcObject = stream;
        streamRef.current = stream; // Keep a reference to stop later
      })
      .catch((err) => {
        console.error("Error accessing the camera: ", err);
        alert("Could not access the camera. Please allow camera permissions.");
      });
  }, []);

  const stopCapture = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((track) => track.stop());
      videoRef.current.srcObject = null;
      streamRef.current = null;
    }
  };

  return (
    <>
      <div className="container my-4 flex justify-center items-center">
        <div className="container flex flex-col justify-center items-center box-border">
          {a.notes.map((note) => {
            return (
              <div
                key={note._id}
                className={`transition-all duration-300 ease-in-out transform ${
                  selectedNote === note._id ? "scale-105 border-2 border-green-500" : ""
                }`}
              >
                <Noteitem
                  key={note._id}
                  note={note}
                  name={note.name}
                  party={note.party}
                  handleonClick2={props.handleonClick2}
                  handleonclick={handleonclick}
                  disable={disable}
                />
              </div>
            );
          })}
        </div>
        <div>
          <button
            className={`btn btn-primary relative bottom-5 ml-3 px-5 py-2 rounded-lg transition-opacity duration-300 ${
              disable ? "opacity-50 cursor-not-allowed" : "hover:scale-105"
            } ${isSubmitting ? "bg-green-500" : "bg-blue-500"}`}
            onClick={handleonclick2}
            disabled={disable || isSubmitting}
          >
            {isSubmitting ? "Submitting..." : "Submit"}
          </button>
        </div>
      </div>

      {/* Camera feed with fade-in transition */}
      <div className={`absolute top-52 transition-opacity duration-500 ease-in-out ${streamRef.current ? 'opacity-100' : 'opacity-0'}`}>
        <video
          ref={videoRef}
          width="320"
          height="240"
          autoPlay
          className="border-2 border-blue-500 rounded-lg"
        ></video>
      </div>
    </>
  );
}
