import React, {useRef, useState} from "react";
import Modal from "./Modal";
//import { Link} from "react-router-dom";

export default function Market(props) {
  const ref = useRef("null");
  const refclose = useRef("null");
  const [notes, setnotes] = useState({
    id:"",
    name:"",
    title: "",
    seller: "",
    price: "",
    img:"",
  });
  const onchange=(e)=>{
    setnotes({...notes,[e.target.name]:e.target.value})
  }
  const handleoninfo=(e)=>{
    //e.preventDefault();
    setnotes({
      id:props.id,
      name:props.name,
      title: props.title,
      seller:props.seller,
      price:props.price,
      img:props.img
    });
    console.log(notes);
    ref.current.click();
  }
  
  return (
    <>
    <button
        type="button"
        className="btn btn-primary"
        data-bs-toggle="modal"
        data-bs-target="#exampleModal"
        ref={ref}
        style={{ display: "none" }}
      >
        More on a Crop
      </button>
      <div
        className="modal fade"
        id="exampleModal"
        tabIndex="-1"
        aria-labelledby="exampleModalLabel"
        aria-hidden="true"
        style={{marginTop:"50px"}}
      >
        <div className="modal-dialog">
          <div className="modal-content" style={{background:"linear-gradient(#E4F1E8,white)"}}>
            <div className="modal-header">
              <h1 className="modal-title fs-5" id="exampleModalLabel">
              More ...
              </h1>
              <button
                type="button"
                className="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>
            <div className="modal-body">
              <Modal notes={notes} onchange={onchange}/>
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-secondary"
                data-bs-dismiss="modal"
                ref={refclose}
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
      <div className="col-md-2" style={{margin:"10px 10px 10px 10px"}} onClick={handleoninfo}>
        <div className="card my-3" style={{ backgroundColor: "#E4F1E8" }}>
          <img src={props.img} className="card-img-top" alt="..." />
          <div className="card-body">
            <h5 className="card-title">{props.name}</h5>
            <p className="card-text">{props.title}</p>
            <p className="card-text">
              {"Seller-"}
              {props.seller}
            </p>
            <p className="card-text">
              {"Price-"}
              {props.price}
            </p>
            <div
              className="d-flex justify-content-end p"
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                flexDirection: "row",
              }}
            >
              <i className="fa-solid fa-cart-shopping "></i>
              <p
                style={{
                  cursor: "pointer",
                  marginTop: "10px",
                  marginLeft: "2px",
                }}
              >
                Add to the cart
              </p>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
