// TidioChat.js
import React, { useEffect } from 'react';

const TidioChat = () => {
  useEffect(() => {
    // Function to add the Tidio script to the document
    const loadTidioScript = () => {
      // Check if the script is already added to prevent multiple instances
      if (document.getElementById('tidio-script')) return;

      // Create a script element
      const script = document.createElement('script');
      script.src = '//code.tidio.co/pevyscxnhqid7r8qaj7zybnxm0h0lryp.js';
      script.async = true;
      script.id = 'tidio-script'; // Set an ID to prevent duplicates
      document.body.appendChild(script); // Append the script to the document body
    };

    loadTidioScript(); // Load the script on component mount

    // Cleanup function to remove the script when the component unmounts (optional)
    return () => {
      const tidioScript = document.getElementById('tidio-script');
      if (tidioScript) {
        tidioScript.remove();
      }
    };
  }, []);

  return null; // This component does not need to render anything
};

